import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.scss'; // Import the SCSS file

const Header = () => {
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const hash = location.hash;
    if (hash) {
      const element = document.querySelector(hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, [location]);

  return (
    <header className="header">
      <div className="container">
        <h1 className="logo">Logo</h1>

        {/* Hamburger Icon for Mobile */}
        <div className="mobile-menu-button">
          <button
            onClick={() => setMenuOpen(!menuOpen)}
            className="focus:outline-none"
          >
            <svg className="hamburger-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>

        {/* Navigation Menu for larger screens */}
        <nav className="nav-menu">
          <ul>
            <li>
              <Link to="/#home">Home</Link>
            </li>
            <li>
              <Link to="/#about">About</Link>
            </li>
            <li>
              <Link to="/team-structure#team">Team Structure</Link>
            </li>
            <li>
              <Link to="/meetings#meetings">Meetings</Link>
            </li>
            <li>
              <Link to="/documents#documents">Documents</Link>
            </li>
          </ul>
        </nav>
      </div>

      {/* Sidebar for mobile screens */}
      <div
        className={`overlay ${menuOpen ? 'open' : ''}`}
        onClick={() => setMenuOpen(false)}
      ></div>

      <nav className={`mobile-sidebar ${menuOpen ? 'open' : ''}`}>
        <div className="p-4">
          <button
            onClick={() => setMenuOpen(false)}
            className="focus:outline-none close-button"
          >
            <svg className="close-button" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
          <ul className="space-y-4">
            <li>
              <Link to="/#home" onClick={() => setMenuOpen(false)}>Home</Link>
            </li>
            <li>
              <Link to="/#about" onClick={() => setMenuOpen(false)}>About</Link>
            </li>
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Header;
